export default function herosReducer(state = { heros: [], info: [], loading: false }, action) {
  switch (action.type) {
    case 'LOADING_HEROS':
      return { ...state, loading: true}

    case 'FETCH_HEROS':
      let heros = action.payload.members
      delete action.payload.members
      let info = action.payload
      return { heros: heros, info: info, loading: false}

    case 'DELETE_ALL_HEROS':
      return { heros: [], loading: false }

    default:
      return state
  }
}
