import React, { Component } from 'react';
import CustomerForm from '../components/CustomerForm'
import { Collapse, Button, CardBody, Card, Row, Col } from 'reactstrap';

class AccordionContainer extends Component {

	constructor(props) {
		super(props);
		this.state = {
		  customerFormVisible: true
	 	}

	 	this.initialState = this.state;    
	}

	formVisibleCntrl = () => {
		// use form.minimize and form.maximize depending on min max count?
		this.setState(prevState => ({
		  customerFormVisible: !prevState.customerFormVisible
		}));
	}

	render(){
		return(
			<div id="customer-form-container">
        <Card>
        	<CardBody>
						<div class="accordion-header" onClick={ () => { this.formVisibleCntrl() } }>
							<h2 class="accordion-title">
								{ this.props.title }
								<Button outline className="float-right edit-form-button" size="sm">edit</Button>
							</h2>
						</div>

						<Collapse isOpen={ this.state.customerFormvisible }>
							{/* function will either output Customer Form or Hero's Panel */}
							<CustomerForm visibleCntrl={ this.visibleCntrl } visible={ this.state.visible }/>
						</Collapse>
					</CardBody>
		    </Card>
			</div>
		)
	}
}

export default AccordionContainer
