export default function herosReducer(state = { heros: [], info: [], loading: false }, action) {
  switch (action.type) {
    case 'LOADING_HEROS':
      return { ...state, loading: true}

    case 'FETCH_HEROS':
      debugger
      return { heros: [action.payload.members], info: [action.payload.squadName, action.payload.secretBase, action.payload.active, action.payload.formed, action.payload.homeTown], loading: false}

    case 'DELETE_ALL_HEROS':
      return { heros: [], loading: false }

    default:
      return state
  }
}
